import React, { Component } from 'react';
import Button from '../../UI/Button/Button';
import classes from './MenuControls.module.css';

class MenuControls extends Component {

    render() {
        return (
            <React.Fragment>
                <div className={classes.Controls}>
                    <Button btnType="ButtonMenu" clicked={this.props.categorySelected("appetizers")}>APPETIZERS</Button>
                    <Button btnType="ButtonMenu" clicked={this.props.categorySelected("mains")}>MAINS</Button>
                    <Button btnType="ButtonMenu" clicked={this.props.categorySelected("beverages")}>BEVERAGES</Button>
                    <Button btnType="ButtonMenu" clicked={this.props.categorySelected("liquor")}>LIQUOR</Button>
                </div>
            </React.Fragment>
        );
    }
         
    
}

export default MenuControls;
