import React from 'react';
import classes from './Toolbar.module.css';
import Logo from '../../UI/Logo/Logo';
import classnames from 'classnames';
import NavigationItems from '../NavigationItems/NavigationItems';
import SideDrawerToggler from '../SideDrawerToggler/SideDrawerToggler';

const Toolbar = (props) => {
    let desktopClasses = classnames(classes.DesktopOnly);

    
    classnames(this.props.className.split(" ").map(name => {
        return classes[name];
    }));
    return (
        <div>
            <header className={classes.Toolbar}>
                {/* Side Drawer */}
                <SideDrawerToggler open={props.togglerClicked}/>

                {/* Logo */}
                <div className={classes.Logo}>
                    <Logo />
                </div>

                {/* Navigation bar */}
                <nav className={classes.DesktopOnly}>
                    <NavigationItems isAuthenticated={props.isAuthenticated} />
                </nav>
            </header>
            
        </div>
        
    );
}

export default Toolbar;
