import React, { Component } from 'react';
import HeaderBox from '../../components/UI/HeaderBox/HeaderBox';
import MenuControls from '../../components/Menu/MenuControls/MenuControls';
import MenuListing from '../../components/Menu/MenuListing/MenuListing';

class MenuContainer extends Component {
    state = {
        category: 'mains',
        categories: {
            appetizers: {
                label: "APPETIZERS",
                btnType: "ButtonMenu",
                activeBtnType: "ButtonMenuActive"
            },
            mains: {
                label: "MAINS",
                btnType: "ButtonMenu",
                activeBtnType: "ButtonMenuActive"
            },
            beverages: {
                label: "BEVERAGES",
                btnType: "ButtonMenu",
                activeBtnType: "ButtonMenuActive"
            },
            liquor: {
                label: "LIQUOR",
                btnType: "ButtonMenu",
                activeBtnType: "ButtonMenuActive"
            }
        }
    }

    handleCategoryChanged = (category) => {
        console.log("received: " + category);
        this.setState({category: category});
    }

    render() {
        return (
            <React.Fragment>
                {/* Header box */}
                <HeaderBox  link="menu"
                            title="MENU"
                            caption="FOOD & DRINKS"
                >
                </HeaderBox>

                {/* Menu Controls */}
                <MenuControls categorySelected={this.handleCategoryChanged} activeCategory={this.state.category}/>

                {/* Menu Listing */}
                <MenuListing />

            </React.Fragment>
        );
    }
}

export default MenuContainer;
