import React, { Component } from 'react';
import Button from '../../UI/Button/Button';
import classes from './MenuControls.module.css';

class MenuControls extends Component {

    render() {
        const controls = Object.keys(this.props.categories).map(ckey => {
            return (
                <Button 
                    btnType={this.props.activeCategory===ckey ? 
                        this.props.categories[key].activeBtnType 
                        : this.props.categories[key].btnType}
                    clicked={() => this.props.categorySelected(ckey)}
                    >
                    {this.props.categories[key].label}
                </Button>
            );
        });
        

        return (
            <React.Fragment>
                <div className={classes.Controls}>
                    <Button 
                        btnType={this.props.activeCategory==='appetizers'?"ButtonMenuActive":"ButtonMenu"}
                        clicked={() => this.props.categorySelected("appetizers")}>APPETIZERS</Button>
                    <Button 
                        btnType={this.props.activeCategory==='mains'?"ButtonMenuActive":"ButtonMenu"} 
                        clicked={() => this.props.categorySelected("mains")}>MAINS</Button>
                    <Button 
                        btnType={this.props.activeCategory==='beverages'?"ButtonMenuActive":"ButtonMenu"} 
                        clicked={() => this.props.categorySelected("beverages")}>BEVERAGES</Button>
                    <Button 
                        btnType={this.props.activeCategory==='liquor'?"ButtonMenuActive":"ButtonMenu"}
                        clicked={() => this.props.categorySelected("liquor")}>LIQUOR</Button>
                </div>
            </React.Fragment>
        );
    }    
}

export default MenuControls;
