import React, { Component } from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import MenuItem from '../MenuItem/MenuItem';
import classes from './MenuListing.module.css';

class MenuListing extends Component {
    render() {
        //console.log(this.props.dishes);
        //split array into pair of row each
        let rows = [];
        let cols = [];
        let count = 1;
        for (let dish in this.props.dishes) {
            if (count%2) {
                // add to row
                rows.push(cols);
                // reset col array
                cols = [];
            } else {
                cols.push(dish);
            }
            // for last elements
            if (count === len(this.props.dishes) && len(cols) > 0) {
                rows.push(cols);
                cols = [];
            }
            count++;
        }
        // transform!!
        let listing = rows.map(row, idx => {
            return (
                <Row key={idx}>
                    {
                        row.map(col, idx => {
                            return (
                                <Col>
                                    <MenuItem key={idx}
                                        imageSrc={col.image}
                                        title={col.dish}
                                        price={col.price}
                                        description={col.description}
                                    />
                                </Col>
                            );
                        })
                    }
                </Row>
            );
