import React, { Component } from 'react';
import Button from '../../UI/Button/Button';
import classes from './MenuControls.module.css';

class MenuControls extends Component {

    menuSelectionHandler = (type) => {
        console.log(type);
    }

    render() {
        return (
            <React.Fragment>
                <div className={classes.Controls}>
                    <Button btnType="ButtonMenu" >APPETIZERS</Button>
                    <Button btnType="ButtonMenu">MAINS</Button>
                    <Button btnType="ButtonMenu">BEVERAGES</Button>
                    <Button btnType="ButtonMenu">LIQUOR</Button>
                </div>
            </React.Fragment>
        );
    }
         
    
}

export default MenuControls;
