    render() {
        const displayCols = 2;
        //split array into pair of row each
        let rows = [];
        let cols = [];
        let count = 1;
        
        this.props.dishes.forEach(dish => {
                if (count%2===0) {
                    console.log(count);
                    // add to cols
                    cols.push(dish);
                    // add to rows
                    rows.push(cols);
                    // reset cols array
                    cols = [];
