import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../utility';

const initialState = {
    //data
    user: null,
    selectedEmployeeId: null,
    selectedEmployee: null,
    employees: [],
    adminRoles: ['sysadmin','stadmin'],
    vehicleInspections: [],
    createdVehicleInspection: null,

    //error handling
    fetchError: null,
    saveError: null,
    //UI handling
    fetchLoading: false,
    saveLoading: false,
};

//fetch employee from 
const fetchEmployeeStart = (state, action) => {
    return updateObject(state, {employee: null, fetchError: null, fetchLoading: true});
}

const fetchEmployeeSuccess = (state, action) => {
    return updateObject(state, {employee: action.employee, fetchError: null, fetchLoading: false});
}

const fetchEmployeeFailed = (state, action) => {
    return updateObject(state, {employee: null, fetchError: action.error, fetchLoading: false});
}

//fetch vehicle inspection
const fetchVehicleInspectionsStart = (state, action) => {
    return updateObject(state, {vehicleInspections: null, fetchError: null, fetchLoading: true});
}

const fetchVehicleInspectionsSuccess = (state, action) => {
    return updateObject(state, {vehicleInspections: action.vehicleInspections, fetchError: null, fetchLoading: false});
}

const fetchVehicleInspectionsFailed = (state, action) => {
    return updateObject(state, {vehicleInspections: null, fetchError: action.error, fetchLoading: false});
}

//create vehicle inspection
const createVehicleInspectionStart = (state, action) => {
    return updateObject(state, {createdVehicleInspection: null, saveError: null, saveLoading: true});
}

const createVehicleInspectionSuccess = (state, action) => {
    return updateObject(state, {createdVehicleInspection: action.createdVehicleInspection, saveError: null, saveLoading: false});
}

const createVehicleInspectionFailed = (state, action) => {
    return updateObject(state, {createdVehicleInspection: null, saveError: action.error, saveLoading: false});
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.FETCH_EMPLOYEE_BY_USER_ID_START: return fetchEmployeeStart(state, action);
        case actionTypes.FETCH_EMPLOYEE_BY_USER_ID_SUCCESS: return fetchEmployeeSuccess(state, action);
        case actionTypes.FETCH_EMPLOYEE_BY_USER_ID_FAILED: return fetchEmployeeFailed(state, action);
        case actionTypes.FETCH_VEHICLE_INSPECTION_START: return fetchVehicleInspectionsStart(state, action);
        case actionTypes.FETCH_VEHICLE_INSPECTION_SUCCESS: return fetchVehicleInspectionsSuccess(state, action);
        case actionTypes.FETCH_VEHICLE_INSPECTION_FAILED: return fetchVehicleInspectionsFailed(state, action);
        case actionTypes.CREATE_VEHICLE_INSPECTION_START: return createVehicleInspectionStart(state, action);
        case actionTypes.CREATE_VEHICLE_INSPECTION_SUCCESS: return createVehicleInspectionSuccess(state, action);
        case actionTypes.CREATE_VEHICLE_INSPECTION_FAILED: return createVehicleInspectionFailed(state, action);
        default: return state;
    }
};

export default reducer;
