import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../utility';

const initialState = {
    user: null,
    fetchError: null,
    fetchLoading: false,
};

const fetchUserStart = (state, action) => {
    return updateObject(state, {user: null, fetchError: null, fetchLoading: true});
}

const fetchUserSuccess = (state, action) => {
    return updateObject(state, {user: action.user, fetchError: null, fetchLoading: false});
}

const fetchUserFailed = (state, action) => {
    return updateObject(state, {user: null, fetchError: null, fetchLoading: false});
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.FETCH_USER_START: return fetchUserStart(state, action);
        case actionTypes.FETCH_USER_SUCCESS: return fetchUserSuccess(state, action);
        case actionTypes.FETCH_USER_FAILED: return fetchUserFailed(state, action);
        default: return state;
    }
};

export default reducer;
