            return (
                <Row key={ridx}>
                    {
                        row.map((col, cidx) => {
                            if (Object.keys(col).length !== 0) {
                                // valid column
                                return (
                                    <Col key={cidx}>
                                        <MenuItem 
                                            imageSrc={col.image}
                                            title={col.dish}
                                            price={col.price}
                                            description={col.description}
                                        />
                                    </Col>
                                );
                            } else {
                                // filled column
                                return (
                                    <Col key={cidx}></Col>
                                )
                            }
                            
                        })
