import React, { Component }  from 'react';
import Layout from './hoc/Layout/Layout';
import { Switch, Route, withRouter, Redirect  } from 'react-router-dom';
import EmployeeContainer from './containers/EmployeeContainer/EmployeeContainer';
import VehicleInspectionContainer from './containers/VehicleInspectionContainer/VehicleInspectionContainer';
import ProfileContainer from './containers/ProfileContainer/ProfileContainer';
import ReportContainer from './containers/ReportContainer/ReportContainer';
import Auth from './containers/Auth/Auth'
import Logout from './containers/Auth/Logout/Logout'
import { connect } from 'react-redux';
import * as actions from './store/actions/';

class App extends Component {
  componentDidMount() {
    this.props.onAuthCheckState();
  }

  render() {
    return (
      <div>

          <Layout>
            <Switch>
              <Route path='/employee' component={EmployeeContainer} />
              <Route path='/login' component={Auth} />
              <Route path='/logout' component={Logout} />
              <Route path='/vehicleInspection' component={VehicleInspectionContainer} />
              <Route path='/profile' component={ProfileContainer} />
              <Route path='/report' component={ReportContainer} />

            </Switch>


            {/* <Button btnType="ButtonWhite">White Button</Button>
            <Button btnType="ButtonBlue">Blue Button</Button>
            <Button btnType="ButtonForm">Form Button</Button> */}
          </Layout>

      </div>
    );

  }

}

const mapStateToProps = state => {
  return {
    isAuthenticated: state.auth.token !== null
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onAuthCheckState: () => dispatch(actions.authCheckState())
  }
}


export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App));
